import React, { useState } from "react";
import "./Login.css";

function Login() {
  const [showLogin, setShowLogin] = useState(true);

  const handleLoginClick = () => {
    setShowLogin(true);
  };

  const handleRegisterClick = () => {
    setShowLogin(false);
  };

  return (
    <div className="main">
      <div className="wrap">
        <div className="heading">
          <h3 className="heading">
            Muthoot Institute of Technology and Science
          </h3>
        </div>
        <div className="portal">
          <h3 className="first">Welcome to</h3>
          <h1 className="mainhead">MITS Placement Portal</h1>
          <p className="first">where hardwork meets result</p>
        </div>
      </div>
      <div className="sub">
        {showLogin ? (
          <LoginComponent onRegisterClick={handleRegisterClick} />
        ) : (
          <RegisterComponent onLoginClick={handleLoginClick} />
        )}
      </div>
    </div>
  );
}

// Login component
function LoginComponent({ onRegisterClick }) {
  return (
    <div className="login">
      <h2 className="login1">Login</h2>
      <div className="details">
        <label>Email</label>
        <input
          type="email"
          placeholder="Username@gmail.com"
          className="email"
        />
        <label>Password</label>
        <input type="password" placeholder="Password" className="password" />
        <p> Forgot Password? </p>
      </div>
      <div>
        <button className="button">Sign in</button>
        <button className="button register-button" onClick={onRegisterClick}>
          Register
        </button>
      </div>
    </div>
  );
}

// Register component
function RegisterComponent({ onLoginClick }) {
  return (
    <div className="register">
      <h2 className="login1">Register</h2>
      <div className="details">
        <label>Email</label>
        <input
          type="email"
          placeholder="Username@gmail.com"
          className="email"
        ></input>
      </div>
      <div>
        <button className="button">Confirm</button>
      </div>
      <div className="otp1">
        <label>Enter the otp</label>
        <input type="text" placeholder="Otp" className="otp1" />
      </div>
      <div>
        <button className="button" onClick={onLoginClick}>
          Continue
        </button>
      </div>
    </div>
  );
}

export default Login;
