import React from 'react'

export default function CloseIcon() {
  return (
    <svg viewBox='0 0 10 10' className='msl-close-icn'>
      <line
        stroke='currentColor'
        strokeLinecap='round'
        x1='0'
        y1='0'
        x2='10'
        y2='10'
      />
      <line
        stroke='currentColor'
        strokeLinecap='round'
        x1='0'
        y1='10'
        x2='10'
        y2='0'
      />
    </svg>
  )
}
import React from 'react'
import CloseIcon from './CloseIcon'

export default function Chip({ value, deleteAction }) {
  const showChipText = (opt) => {
    if (typeof opt.label === 'object') {
      return opt?.title || opt.value
    } else {
      return opt.label
    }
  }

  return (
    <div clickable='true' className='msl-chip'>
      {showChipText(value)}
      <div
        role='button'
        clickable='true'
        aria-label='delete-value'
        onClick={deleteAction}
        onKeyPress={deleteAction}
        tabIndex='0'
        className='msl-btn msl-chip-delete msl-flx'
      >
        <CloseIcon />
      </div>
      <span />
    </div>
  )
}
import React from 'react'

export default function DownIcon() {
  return (
    <svg viewBox='0 0 20 20' className='msl-arrow-icn'>
      <line
        stroke='currentColor'
        strokeLinecap='round'
        x1='10'
        y1='14'
        x2='4'
        y2='8'
      />
      <line
        stroke='currentColor'
        strokeLinecap='round'
        x1='16'
        y1='8'
        x2='10'
        y2='14'
      />
    </svg>
  )
}
import React from 'react'

function Options({
  opts,
  singleSelect,
  addValue,
  checkValueExist,
  value,
  largeData,
  menuOpen
}) {
  if (largeData && !menuOpen) return null
  const optsArr = []
  function addInArr(opts) {
    for (const [i, opt] of opts.entries()) {
      if (opt.type === 'group') {
        optsArr.push(
          <div key={opt.title + i} data-msl className='msl-grp-title'>
            {opt.title}
          </div>
        )
        if (opt.childs.length > 0) {
          addInArr(opt.childs)
        } else {
          optsArr.push(
            <option className='msl-option msl-option-disable'>
              {opt.emptyDataLabel || 'No Data Found'}
            </option>
          )
        }
      } else {
        optsArr.push(
          <div
            tabIndex='0'
            key={opt.value + opt.label + i + 10}
            {...(!singleSelect && { 'data-msl': true })}
            style={{
              ...(opt.style && opt.style)
            }}
            onClick={() => {
              !opt.disabled && addValue(opt)
            }}
            onKeyPress={(e) => {
              e.key === 'Enter' && !opt.disabled && addValue(opt)
            }}
            title={typeof opt.label === 'object' ? opt?.title : opt.label}
            className={`msl-option ${checkValueExist(opt, value) ? 'msl-option-active' : ''
              } ${opt.disabled ? 'msl-option-disable' : ''} ${opt.classes !== undefined ? opt.classes : ''
              }`}
            value={opt.value}
          >
            {opt.label}
          </div>
        )
      }
    }
  }
  addInArr(opts)
  return <div>{optsArr}</div>
}

export default Options
